import React from 'react'
import { CartItem } from '~/redux/slices'
import { Button, InputNumber, Image } from 'antd'
import { ArrowRightOutlined, ArrowLeftOutlined } from '@ant-design/icons'
import { formatCurrencyVND } from '~/utils/numberUtils'
import { getBirdImage } from '~/utils/imageUtils'
import { useCart } from '~/application/hooks/useCart'

type CartItemProps = {
  item: CartItem
}

const CartItemCard: React.FC<CartItemProps> = ({ item }) => {
  console.log(item)
  const { adjustQuantity, removeFromCart } = useCart()
  const onChange = (value: number | null) => {
    if (value) {
      adjustQuantity({ id: item.id, quantity: value })
    }
  }
  const increase = () => {
    if (item.quantity < item.detail!.quantity) {
      adjustQuantity({ id: item.id, quantity: item.quantity + 1 })
    }
  }
  const decrease = () => {
    if (item.quantity > 1) {
      adjustQuantity({ id: item.id, quantity: item.quantity - 1 })
    }
  }
  const remove = () => {
    removeFromCart(item.id)
  }
  return (
    <>
      <div className='w-[15%] mx-[2%] h-full'>
        <Image
          src={getBirdImage(item.detail?.thumbnail)}
          className='text-center w-full h-full object-cover'
          preview={false}
          loading='eager'
          fallback='data:image/png;base64,'
        />
      </div>
      <div className=' space-y-2 w-[15%] flex-col mt-0'>
        <p className='font-semibold text-base'>{item.detail?.name}</p>
      </div>
      <div className='w-[5%]'></div>
      <div className='ml-[10%] w-[8%]'>
        <p>{formatCurrencyVND(item.price)}</p>
      </div>
      <div className='space-x-1 w-[15%] flex justify-center'>
        <Button icon={<ArrowLeftOutlined />} onClick={decrease} />
        <InputNumber controls={false} value={item.quantity} className='!w-[25%]' onChange={onChange} />
        <Button icon={<ArrowRightOutlined />} onClick={increase} />
      </div>
      <div className='w-[15%]'>
        <p>{item.price * item.quantity}</p>
      </div>
      <div className='w-[10%]'>
        <Button type='link' onClick={remove}>
          XÃ³a
        </Button>
      </div>
    </>
  )
}

export default CartItemCard
