import {
  App,
  Skeleton,
  Result,
  Space,
  Breadcrumb,
  Steps,
  Typography,
  Flex,
  Divider,
  List,
  Image,
  Descriptions,
  Button
} from 'antd'
import React, { useState, useMemo, useEffect } from 'react'
import { Link, useParams } from 'react-router-dom'
import { useAppDispatch } from '~/application/hooks/reduxHook'
import useFetchData from '~/application/hooks/useFetchData'
import type { StepProps, DescriptionsProps } from 'antd'
import {
  ShoppingCartOutlined,
  InboxOutlined,
  CarOutlined,
  CarryOutOutlined,
  CheckCircleOutlined
} from '@ant-design/icons'
import axiosClient from '~/utils/api/AxiosClient'
import { getBirdImage } from '~/utils/imageUtils'
import { BookingStatus } from '~/application/components/shared/constanst'
import { reFetchData } from '~/redux/slices'

interface OrderStep {
  status: 'wait' | 'error' | 'process' | 'finish'
  current: number
}
interface BirdImage {
  id: number
  imageUrl: string
}

interface Category {
  id: number
  name: string
}

interface BirdType {
  id: number
  name: string
}

interface Bird {
  createdAt: string
  updatedAt: string
  id: number
  name: string
  price: number
  thumbnail: string
  description: string
  category: Category
  birdType: BirdType
  status: boolean
  purebredLevel: string
  competitionAchievements: number
  age: string
  gender: string
  color: string
  quantity: number
  birdImages: BirdImage[]
}

interface BirdPairing {
  createdAt: string
  updatedAt: string
  id: number
  newBird: Bird
  status: string
}

interface BookingDetail {
  createdAt: string
  updatedAt: string
  id: number
  birdType: BirdType
  fatherBird: Bird
  motherBird: Bird
  status: string
  birdPairing: BirdPairing[]
}

interface ShippingMethod {
  createdAt: string
  updatedAt: string
  id: number
  name: string
  shippingMoney: number
}

interface RoleEntity {
  id: number
  name: string
}

interface User {
  createdAt: string
  updatedAt: string
  id: number
  fullName: string
  phoneNumber: string
  email: string
  address: string
  imageUrl: string
  roleEntity: RoleEntity
  emailVerified: boolean
  dob: string
  isActive: number
}

interface Booking {
  createdAt: string
  updatedAt: string
  id: number
  user: User
  fullName: string
  phoneNumber: string
  shippingAddress: string
  paymentMethod: string
  status: string
  paymentDeposit: number
  totalPayment: number
  bookingDetail: BookingDetail
  shippingMethod: ShippingMethod
  trackingNumber: string
}
const BookingDetailPage: React.FC = () => {
  const { id } = useParams()
  const { notification } = App.useApp()
  const dispatch = useAppDispatch()
  // const [loadingButton, setLoadingButton] = useState<boolean>(false)
  const [loading, error, response] = useFetchData(`/booking/${id}`)

  const [btnPaymentLoading, setBtnPaymentLoading] = useState<boolean>(false)

  const [orderStep, setOrderStep] = useState<OrderStep>({
    status: 'process',
    current: 3
  })

  const booking: Booking = useMemo(() => {
    if (!loading && !error && response) {
      return response.data
    }
    return null
  }, [loading, error, response])
  console.log(booking)
  const pairings: BirdPairing[] = useMemo(() => {
    if (booking) return booking.bookingDetail.birdPairing
    return []
  }, [booking])

  const stepItems: StepProps[] = [
    {
      title: orderStep.current > 0 ? 'Đã trả trước' : 'Chờ trả trước',
      icon: orderStep.current === 0 && orderStep.status === 'error' ? null : <ShoppingCartOutlined />
    },
    {
      title: orderStep.current === 1 ? 'Đang lai chim' : orderStep.current > 1 ? 'Đã lai xong' : 'Chờ lai chim',
      icon: orderStep.current === 1 && orderStep.status === 'error' ? null : <CheckCircleOutlined />
    },
    {
      title: orderStep.current === 2 ? 'Đang nhận con' : orderStep.current > 2 ? 'Đã nhận con' : 'Chờ nhận con',
      icon: orderStep.current === 2 && orderStep.status === 'error' ? null : <InboxOutlined />
    },
    {
      title: orderStep.current === 3 ? 'Đang vận chuyển' : orderStep.current > 3 ? 'Đã vận chuyển' : 'Chờ vận chuyển',
      icon: orderStep.current === 3 && orderStep.status === 'error' ? null : <CarOutlined />
    },
    {
      title: orderStep.current === 4 ? 'Đang giao hàng' : orderStep.current > 4 ? 'Đã giao hàng' : 'Chờ giao hàng',
      icon: orderStep.current === 4 && orderStep.status === 'error' ? null : <CarryOutOutlined />
    }
  ]

  const handleRepay = () => {
    setBtnPaymentLoading(true)
    axiosClient
      .get(`/booking/pay-unpaid-booking?id=${id}`)
      .then((response) => {
        setBtnPaymentLoading(false)
        if (response) {
          notification.success({ message: 'Vui lòng thanh toán trả trước' })

          if (response.data.url) {
            window.open(response.data.url, '_blank')!.focus()
          }
          dispatch(reFetchData())
        } else {
          notification.error({ message: 'Thanh toán thất bại' })
        }
      })
      .catch((err) => {
        setBtnPaymentLoading(false)
        notification.error({ message: (err as string) || 'Sorry! Something went wrong. App server error' })
      })
  }
  const handlePay = () => {
    setBtnPaymentLoading(true)
    axiosClient
      .get(`/booking/pay-total-booking?id=${id}`)
      .then((response) => {
        setBtnPaymentLoading(false)
        if (response) {
          notification.success({ message: 'Vui lòng thanh toán trả trước' })

          if (response.data.url) {
            window.open(response.data.url, '_blank')!.focus()
          }
          dispatch(reFetchData())
        } else {
          notification.error({ message: 'Thanh toán thất bại' })
        }
      })
      .catch((err) => {
        setBtnPaymentLoading(false)
        notification.error({ message: (err as string) || 'Sorry! Something went wrong. App server error' })
      })
  }

  useEffect(() => {
    if (!loading && !error && response) {
      const status = response.data.status as BookingStatus

      if (status == BookingStatus.Pending) {
        setOrderStep({
          status: 'process',
          current: 0
        })
      } else if (status == BookingStatus.Confirmed) {
        setOrderStep({
          status: 'process',
          current: 1
        })
      } else if (status == BookingStatus.Preparing) {
        setOrderStep({
          status: 'process',
          current: 2
        })
      } else if (status == BookingStatus.Shipping) {
        setOrderStep({
          status: 'process',
          current: 3
        })
      } else if (status == BookingStatus.Delivered) {
        setOrderStep({
          status: 'process',
          current: 4
        })
      }
    }
  }, [loading, error, response])
  return (
    <div className='w-full h-full p-10  bg-gray-200'>
      <Skeleton loading={loading} active>
        {error ? (
          <Result title='Failed to fetch' subTitle={error} status='error' />
        ) : (
          <Space className='w-full bg-[#fff] p-10 rounded-xl' direction='vertical' size='large'>
            <Breadcrumb
              items={[
                {
                  title: <Link to='/booking'>Danh sách đơn lai chim</Link>
                },
                {
                  title: `Mã lai chim ${id}`
                }
              ]}
            />
            <Steps
              className='custom-step-content'
              // onChange={onStepChange}
              current={orderStep.current}
              status={orderStep.status}
              labelPlacement='vertical'
              items={stepItems}
            />
            <div>
              {booking?.status === BookingStatus.Pending && (
                <Flex justify='flex-end' className='border-[1px] border-dashed p-5'>
                  <Button className='w-48' size='large' type='primary' loading={btnPaymentLoading} onClick={handlePay}>
                    Thanh toán trả trước
                  </Button>
                </Flex>
              )}
              {booking?.status === BookingStatus.Preparing && (
                <Flex justify='flex-end' className='border-[1px] border-dashed p-5'>
                  <Button
                    className='w-48'
                    size='large'
                    type='primary'
                    loading={btnPaymentLoading}
                    onClick={handleRepay}
                  >
                    Thanh toán nhận chim
                  </Button>
                </Flex>
              )}
            </div>
            <div className='cmp831'>
              <div
                className='DM1xQK'
                style={{
                  height: ' 0.1875rem',
                  width: '100%',
                  backgroundPositionX: '-1.875rem',
                  backgroundSize: '7.25rem 0.1875rem',
                  backgroundImage:
                    'repeating-linear-gradient(45deg,#6fa6d6,#6fa6d6 33px,transparent 0,transparent 41px,#f18d9b 0,#f18d9b 74px,transparent 0,transparent 82px)'
                }}
              ></div>
            </div>
            <Space direction='vertical' className='!w-full px-5'>
              <Flex justify='space-between' align='center'>
                <Typography.Title level={3}>Địa chỉ nhận hàng</Typography.Title>
                <Space direction='vertical'>
                  <Typography.Text>{booking?.shippingMethod.name}</Typography.Text>
                  <Typography.Text>{booking?.trackingNumber}</Typography.Text>
                </Space>
              </Flex>
              <Typography.Text strong>{booking?.fullName}</Typography.Text>
              <Typography.Text type='secondary'>{booking?.phoneNumber}</Typography.Text>
              <Typography.Text type='secondary'>{booking?.trackingNumber}</Typography.Text>
            </Space>

            <Divider />
            <Typography.Title level={3}>Chim Bố x Chim Mẹ</Typography.Title>
            <List itemLayout='horizontal'>
              <List.Item>
                <List.Item.Meta
                  avatar={
                    <Image
                      height={100}
                      src={getBirdImage(booking?.bookingDetail.fatherBird.thumbnail)}
                      fallback='data:image/png;base64,'
                    />
                  }
                  title={<Typography.Title level={5}>{booking?.bookingDetail.fatherBird.name}</Typography.Title>}
                  description={
                    <Descriptions
                      items={[
                        {
                          key: '1',
                          label: 'Giới tính',
                          children: booking?.bookingDetail.fatherBird.gender
                        }
                      ]}
                      column={1}
                    />
                  }
                />
              </List.Item>
              <List.Item>
                <List.Item.Meta
                  avatar={
                    <Image
                      height={100}
                      src={getBirdImage(booking?.bookingDetail.motherBird.thumbnail)}
                      fallback='data:image/png;base64,'
                    />
                  }
                  title={<Typography.Title level={5}>{booking?.bookingDetail.motherBird.name}</Typography.Title>}
                  description={
                    <Descriptions
                      items={[
                        {
                          key: '1',
                          label: 'Giới tính',
                          children: booking?.bookingDetail.motherBird.gender
                        }
                      ]}
                      column={1}
                    />
                  }
                />
              </List.Item>
            </List>

            {booking?.status === BookingStatus.Preparing && (
              <>
                <Divider />
                <Typography.Title level={3}>Chim con</Typography.Title>
                <List
                  itemLayout='horizontal'
                  dataSource={pairings}
                  renderItem={(item) => {
                    if (item.status === 'Egg') {
                      return (
                        <List.Item>
                          <List.Item.Meta
                            avatar={
                              <Image
                                height={100}
                                src={getBirdImage('egg.jpg')}
                                fallback='data:image/png;base64,'
                              />
                            }
                            title={<Typography.Title level={5}>Trứng</Typography.Title>}
                          />
                        </List.Item>
                      )
                    } else {
                      const items: DescriptionsProps['items'] = [
                        {
                          key: '1',
                          label: 'Giới tính',
                          children: item.newBird.gender
                        }
                      ]
                      return (
                        <List.Item>
                          <List.Item.Meta
                            avatar={
                              <Image
                                height={100}
                                src={getBirdImage(item.newBird.thumbnail)}
                                fallback='data:image/png;base64,'
                              />
                            }
                            title={<Typography.Title level={5}>{item.newBird.name}</Typography.Title>}
                            description={<Descriptions items={items} column={1} />}
                          />
                        </List.Item>
                      )
                    }
                  }}
                />
              </>
            )}
          </Space>
        )}
      </Skeleton>
    </div>
  )
}

export default BookingDetailPage
