import { Card, Button, ConfigProvider, Collapse, Image, Row, Col, Flex, Typography, Descriptions, Space } from 'antd'
import React, { useMemo, useState } from 'react'
import type { CollapseProps } from 'antd'
import { DescriptionsProps } from 'antd/lib'
import { getBirdImage } from '~/utils/imageUtils'
import { usePairing } from '~/application/hooks/usePairing'

const { Title, Paragraph } = Typography

type BirdImage = {
  id: number
  imageUrl: string
}

type Bird = {
  createdAt: string
  updatedAt: string
  id: number
  name: string
  price: number
  thumbnail: string
  description: string
  category: {
    id: number
    name: string
  }
  birdType: {
    id: number
    name: string
  }
  status: boolean
  purebredLevel: string
  competitionAchievements: number
  age: string
  gender: string
  color: string
  quantity: number
  birdImages: BirdImage[] // Đây là một mảng các hình ảnh của chim
}

type PairingItemProps = {
  bird: Bird
  type: 'male' | 'female' | 'checkout'
}

const PairingItem: React.FC<PairingItemProps> = ({ bird, type }) => {
  const { pairing, addMotherToPairing, addFatherToPairing, removeFatherFromPairing, removeMotherFromPairing } =
    usePairing()
  const [current, setCurrent] = useState<number>(0)
  const [expand, setExpand] = useState<boolean>(false)
  const selected = useMemo(() => {
    if (type === 'male') return pairing.father
    else return pairing.mother
  }, [pairing.father, pairing.mother, type])
  const details: DescriptionsProps['items'] = [
    {
      key: '1',
      label: 'Độ thuần chủng',
      children: bird?.purebredLevel
    },
    {
      key: '2',
      label: 'Màu sắc',
      children: bird?.color
    },
    {
      key: '3',
      label: 'Tuổi',
      children: bird?.age
    },

    {
      key: '5',
      label: 'Tham gia thi đấu',
      children: bird?.competitionAchievements
    }
  ]
  const collapse: CollapseProps['items'] = [
    {
      key: '1',
      children: <Descriptions column={2} layout='vertical' items={details} />,
      showArrow: false
    }
  ]
  const images = useMemo(() => {
    const d = bird ? bird.birdImages.map((e) => getBirdImage(e.imageUrl)) : []
    return [getBirdImage(bird?.thumbnail), ...d]
  }, [bird])
  const handleToAdd = (bird: Bird) => {
    const payload = {
      id: bird.id.toString(),
      name: bird.name,
      thumbnail: bird.thumbnail
    }

    if (type === 'female') {
      addMotherToPairing(payload)
    }
    if (type === 'male') {
      addFatherToPairing(payload)
    }
  }
  const handleToRemove = () => {
    if (type === 'female') {
      removeMotherFromPairing()
    }
    if (type === 'male') {
      removeFatherFromPairing()
    }
  }
  const renderImageGrid = () => {
    const render = []
    const length = images.length

    if (length === 1) {
      render.push(
        <Col span={24}>
          <Image
            preview={false}
            className='max-w-full w-auto max-h-full h-auto'
            src={images[0]}
            fallback='data:image/png;base64,'
          />
        </Col>
      )
    }

    if (length === 2) {
      render.push(
        <Col span={12}>
          <Image
            onClick={() => setCurrent(0)}
            className='max-w-full w-auto max-h-full h-auto'
            src={images[0]}
            fallback='data:image/png;base64,'
          />
        </Col>
      )
      render.push(
        <Col span={12}>
          <Image
            onClick={() => setCurrent(1)}
            className='max-w-full w-auto max-h-full h-auto'
            src={images[1]}
            fallback='data:image/png;base64,'
          />
        </Col>
      )
    }

    if (length === 3) {
      render.push(
        <Col span={24}>
          <Image
            onClick={() => setCurrent(0)}
            className='max-w-full w-auto max-h-full h-auto'
            src={images[0]}
            fallback='data:image/png;base64,'
          />
        </Col>
      )
      render.push(
        <Col span={12}>
          <Image
            onClick={() => setCurrent(1)}
            className='max-w-full w-auto max-h-full h-auto'
            src={images[1]}
            fallback='data:image/png;base64,'
          />
        </Col>
      )
      render.push(
        <Col span={12}>
          <Image
            onClick={() => setCurrent(2)}
            className='max-w-full w-auto max-h-full h-auto'
            src={images[2]}
            fallback='data:image/png;base64,'
          />
        </Col>
      )
    }

    if (length === 4) {
      render.push(
        <Col span={24}>
          <Image
            onClick={() => setCurrent(0)}
            className='max-w-full w-auto max-h-full h-auto'
            src={images[0]}
            fallback='data:image/png;base64,'
          />
        </Col>
      )
      render.push(
        <Col span={8}>
          <Image
            onClick={() => setCurrent(1)}
            className='max-w-full w-auto max-h-full h-auto'
            src={images[1]}
            fallback='data:image/png;base64,'
          />
        </Col>
      )
      render.push(
        <Col span={8}>
          <Image
            onClick={() => setCurrent(2)}
            className='max-w-full w-auto max-h-full h-auto'
            src={images[2]}
            fallback='data:image/png;base64,'
          />
        </Col>
      )
      render.push(
        <Col span={8}>
          <Image
            onClick={() => setCurrent(3)}
            className='max-w-full w-auto max-h-full h-auto'
            src={images[3]}
            fallback='data:image/png;base64,'
          />
        </Col>
      )
    }

    if (length > 4) {
      render.push(
        <Col span={24}>
          <Image
            onClick={() => setCurrent(0)}
            className='max-w-full w-auto max-h-full h-auto'
            src={images[0]}
            fallback='data:image/png;base64,'
          />
        </Col>
      )
      render.push(
        <Col span={8}>
          <Image
            onClick={() => setCurrent(1)}
            className='max-w-full w-auto max-h-full h-auto'
            src={images[1]}
            fallback='data:image/png;base64,'
          />
        </Col>
      )
      render.push(
        <Col span={8}>
          <Image
            onClick={() => setCurrent(2)}
            className='max-w-full w-auto max-h-full h-auto'
            src={images[2]}
            fallback='data:image/png;base64,'
          />
        </Col>
      )
      render.push(
        <Col span={8} className='show-mask'>
          <Image
            onClick={() => setCurrent(3)}
            preview={{ mask: `+${length - 3}` }}
            className='max-w-full w-auto max-h-full h-auto'
            src={images[3]}
            fallback='data:image/png;base64,'
          />
        </Col>
      )
    }
    return render
  }
  return (
    <Card hoverable className='!w-full'>
      <Row gutter={[16, 16]}>
        <Col span={expand ? '24' : '12'}>
          <Flex justify='center' align='center'>
            {expand ? (
              <Image.PreviewGroup
                preview={{
                  current: current,
                  onChange: (current) => setCurrent(current)
                }}
                items={images}
              >
                <Row className='p-5' gutter={[16, 16]}>
                  {renderImageGrid()}
                </Row>
              </Image.PreviewGroup>
            ) : (
              <Image
                preview={false}
                className='max-w-full w-auto max-h-full h-auto'
                src={getBirdImage(bird?.thumbnail)}
                fallback='data:image/png;base64,'
              />
            )}
          </Flex>
        </Col>
        <Col span={expand ? '24' : '12'}>
          <Title level={5}>Chim sơn ca</Title>
          <Paragraph
            ellipsis={
              !expand
                ? {
                    expandable: false,
                    rows: 4,

                    symbol: 'Xem thêm',
                    onEllipsis: (ellipsis) => {
                      console.log('Ellipsis changed:', ellipsis)
                    }
                  }
                : false
            }
          >
            Chim sơn ca là một loài chim với tiếng kêu líu lo và tươi vui. Chúng thường sống trong môi trường tự nhiên
            và có một bộ lông màu sắc độc đáo. Tiếng kêu của chim sơn ca thường được coi là âm nhạc tự nhiên đáng yêu.
          </Paragraph>
          <ConfigProvider
            theme={{
              components: {
                Collapse: {
                  headerPadding: 0,
                  contentPadding: 0
                }
              }
            }}
          >
            <Collapse items={collapse} ghost bordered={false} activeKey={expand ? ['1'] : []} />
          </ConfigProvider>
          <Space direction='vertical' className='w-full'>
            {type !== 'checkout' &&
              (selected ? (
                <Button type='primary' className='w-full' onClick={() => handleToRemove()}>
                  Hủy
                </Button>
              ) : (
                <Button type='primary' className='w-full' onClick={() => handleToAdd(bird)}>
                  Chọn để ghép
                </Button>
              ))}

            <Button
              className='w-full'
              onClick={() => {
                setExpand((prev) => !prev)
              }}
            >
              {expand ? 'Thu nhỏ' : 'Xem thêm'}
            </Button>
          </Space>
        </Col>
      </Row>
    </Card>
  )
}

export default PairingItem
