import React, { useMemo, useState } from 'react'
import 'react-multi-carousel/lib/styles.css'
import {
  App,
  Button,
  Col,
  Descriptions,
  Image,
  Rate,
  Skeleton,
  Tabs,
  Tag,
  Row,
  Flex,
  Card,
  Typography,
  Result,
  List,
  Space
} from 'antd'
import { useNavigate, useParams } from 'react-router-dom'
import Carousel from 'react-multi-carousel'
import { PlusOutlined, ShoppingCartOutlined, UserOutlined, ShoppingOutlined } from '@ant-design/icons'
import { Avatar } from 'antd'
import useFetchData from '~/application/hooks/useFetchData'
import { formatCurrencyVND } from '~/utils/numberUtils'
import { DescriptionsProps } from 'antd/lib'
import { useCart } from '~/application/hooks/useCart'
import { useCompare } from '~/application/hooks/useCompare'
import { getBirdImage, getUserImage } from '~/utils/imageUtils'

const responsive = {
  desktop: {
    breakpoint: { max: 3000, min: 1024 },
    items: 4,
    slidesToSlide: 2
  },
  mobile: {
    breakpoint: { max: 464, min: 0 },
    items: 1
  }
}

type BirdImage = {
  id: number
  imageUrl: string
}
type Category = {
  id: number
  name: string
}
type BirdType = {
  id: number
  name: string
}
type BirdData = {
  id: number
  name: string
  categoryName: string
  birdType: string
  gender: string
  color: string
  price: number
  quantity: number
  thumbnail: string
  competitionAchievements: number
  purebredLevel: string
  description: string
  totalRating: number
  countRating: number
  sold: number
  bird_images: BirdImage[]
}

type BirdFeedback = {
  active: boolean
  address: string
  birdId: number
  birdName: string
  birdType: string
  comment: string
  createdAt: string
  fullName: string
  id: number
  orderId: number
  phoneNumber: string
  rating: number
  status: boolean
  userImage: string
}
type Bird = {
  createdAt: string
  updatedAt: string
  id: number
  name: string
  price: number
  thumbnail: string
  description: string
  category: Category
  birdType: BirdType
  status: boolean
  purebredLevel: string
  competitionAchievements: number
  age: string
  gender: string
  color: string
  quantity: number
  birdImages: BirdImage[]
}

const ProductDetail: React.FC = () => {
  const { id } = useParams()
  const navigate = useNavigate()
  const { addToCart } = useCart()
  const { addProductToCompare, compare } = useCompare()
  const [current, setCurrent] = useState<number>(0)

  const [loading, error, response] = useFetchData(`/birds/detail/${id}`)
  const [feedbackLoading, feedbackError, feedbackResponse] = useFetchData(`/feedbackbirds/bird/${id}`)
  const [recommendLoading, recommendError, recommendResponse] = useFetchData(`/birds/recommendlist?birdId=${id}&K=20`)
  const { notification } = App.useApp()

  const compareItems = useMemo(
    () => (Object.values(compare.items).length > 0 ? Object.values(compare.items) : []),
    [compare]
  )
  const birdData: BirdData = useMemo(() => {
    if (!loading && !error && response) {
      return response.data
    }
    return []
  }, [loading, error, response])

  const feedbackData: BirdFeedback[] = useMemo(() => {
    if (!feedbackLoading && !feedbackError && feedbackResponse) {
      const f = feedbackResponse.data as BirdFeedback[]
      return f.filter((e) => e.active === true)
    }
    return []
  }, [feedbackLoading, feedbackError, feedbackResponse])

  const recommendBird: Bird[] = useMemo(() => {
    if (!recommendLoading && !recommendError && recommendResponse) {
      return recommendResponse.data
    }
    return []
  }, [recommendLoading, recommendError, recommendResponse])
  const images = useMemo(() => {
    if (!loading && !error && response) {
      const bird = response.data as BirdData
      const d = bird.bird_images.map((e) => getBirdImage(e.imageUrl))
      return [getBirdImage(bird.thumbnail), ...d]
    }
    return []
  }, [loading, error, response])

  const items: DescriptionsProps['items'] = [
    {
      key: '1',
      label: 'Giới tính',
      children: `${birdData?.gender}`
    },
    {
      key: '2',
      label: 'Độ thuần chủng',
      children: `${birdData?.purebredLevel}`
    },
    {
      key: '3',
      label: 'Màu lông',
      children: `${birdData?.color}`
    },
    {
      key: '4',
      label: 'Mô tả',
      span: 2,
      children: `${birdData?.description}`
    }
  ]
  const smallDetail: DescriptionsProps['items'] = [
    {
      key: '1',
      children: `${birdData?.sold} con`,
      label: 'Đã bán'
    },
    {
      key: '2',
      children: feedbackData.length,
      label: 'Bình luận'
    },
    {
      key: '3',
      children: `${birdData?.quantity} con`,
      label: 'Tồn kho'
    }
  ]
  const addToCompare = (bird: BirdData) => {
    if (!compareItems.find((item) => item.id === bird.id.toString())) {
      if (compareItems.length > 0 && compareItems[0].detail?.birdType.name === bird.birdType) {
        addProductToCompare({
          id: bird.id.toString(),
          name: bird.name,
          thumbnail: bird.thumbnail
        })
        notification.success({ message: 'Thêm vào so sánh thành công' })
      } else if (compareItems.length > 0 && compareItems[0].detail?.birdType.name !== bird.birdType) {
        notification.error({ message: 'Không thể so sánh khác loài' })
      } else {
        addProductToCompare({
          id: bird.id.toString(),
          name: bird.name,
          thumbnail: bird.thumbnail
        })
        notification.success({ message: 'Thêm vào so sánh thành công' })
      }
    }
  }

  const handleAddToCart = (bird: BirdData | Bird) => {
    addToCart({
      id: bird.id.toString(),
      price: bird.price
    })
    notification.success({ message: 'Thêm vào giỏ hàng thành công' })
  }
  const renderImageGrid = () => {
    const render = []
    const length = images.length

    if (length === 1) {
      render.push(
        <Col span={24}>
          <Image
            preview={false}
            className='max-w-full w-auto max-h-full h-auto'
            src={images[0]}
            fallback='data:image/png;base64,'
          />
        </Col>
      )
    }

    if (length === 2) {
      render.push(
        <Col span={12}>
          <Image
            onClick={() => setCurrent(0)}
            className='max-w-full w-auto max-h-full h-auto'
            src={images[0]}
            fallback='data:image/png;base64,'
          />
        </Col>
      )
      render.push(
        <Col span={12}>
          <Image
            onClick={() => setCurrent(1)}
            className='max-w-full w-auto max-h-full h-auto'
            src={images[1]}
            fallback='data:image/png;base64,'
          />
        </Col>
      )
    }

    if (length === 3) {
      render.push(
        <Col span={24}>
          <Image
            onClick={() => setCurrent(0)}
            className='max-w-full w-auto max-h-full h-auto'
            src={images[0]}
            fallback='data:image/png;base64,'
          />
        </Col>
      )
      render.push(
        <Col span={12}>
          <Image
            onClick={() => setCurrent(1)}
            className='max-w-full w-auto max-h-full h-auto'
            src={images[1]}
            fallback='data:image/png;base64,'
          />
        </Col>
      )
      render.push(
        <Col span={12}>
          <Image
            onClick={() => setCurrent(2)}
            className='max-w-full w-auto max-h-full h-auto'
            src={images[2]}
            fallback='data:image/png;base64,'
          />
        </Col>
      )
    }

    if (length === 4) {
      render.push(
        <Col span={24}>
          <Image
            onClick={() => setCurrent(0)}
            className='max-w-full w-auto max-h-full h-auto'
            src={images[0]}
            fallback='data:image/png;base64,'
          />
        </Col>
      )
      render.push(
        <Col span={8}>
          <Image
            onClick={() => setCurrent(1)}
            className='max-w-full w-auto max-h-full h-auto'
            src={images[1]}
            fallback='data:image/png;base64,'
          />
        </Col>
      )
      render.push(
        <Col span={8}>
          <Image
            onClick={() => setCurrent(2)}
            className='max-w-full w-auto max-h-full h-auto'
            src={images[2]}
            fallback='data:image/png;base64,'
          />
        </Col>
      )
      render.push(
        <Col span={8}>
          <Image
            onClick={() => setCurrent(3)}
            className='max-w-full w-auto max-h-full h-auto'
            src={images[3]}
            fallback='data:image/png;base64,'
          />
        </Col>
      )
    }

    if (length > 4) {
      render.push(
        <Col span={24}>
          <Image
            onClick={() => setCurrent(0)}
            className='max-w-full w-auto max-h-full h-auto'
            src={images[0]}
            fallback='data:image/png;base64,'
          />
        </Col>
      )
      render.push(
        <Col span={8}>
          <Image
            onClick={() => setCurrent(1)}
            className='max-w-full w-auto max-h-full h-auto'
            src={images[1]}
            fallback='data:image/png;base64,'
          />
        </Col>
      )
      render.push(
        <Col span={8}>
          <Image
            onClick={() => setCurrent(2)}
            className='max-w-full w-auto max-h-full h-auto'
            src={images[2]}
            fallback='data:image/png;base64,'
          />
        </Col>
      )
      render.push(
        <Col span={8} className='show-mask'>
          <Image
            onClick={() => setCurrent(3)}
            preview={{ mask: `+${length - 3}` }}
            className='max-w-full w-auto max-h-full h-auto'
            src={images[3]}
            fallback='data:image/png;base64,'
          />
        </Col>
      )
    }
    return render
  }
  return (
    <div className='object-fill my-0 lg:p-[1%] px-[2%] lg:px-[10%]' style={{ background: '#038777' }}>
      <div className='border-solid border-4 border-gray-100 rounded-t-lg bg-gray-200 p-5'>
        <Skeleton loading={loading} active>
          {error ? (
            <p>Error</p>
          ) : (
            <Row gutter={[16, 16]}>
              <Col span={12}>
                <Image.PreviewGroup
                  preview={{
                    current: current,
                    onChange: (current) => setCurrent(current)
                  }}
                  items={images}
                >
                  <Flex justify='center' align='center'>
                    <Row className='p-5' gutter={[16, 16]}>
                      {renderImageGrid()}
                    </Row>
                  </Flex>
                </Image.PreviewGroup>
              </Col>
              <Col span={12}>
                <div className='flex flex-col w-full object-cover px-5'>
                  <div className='text-lg space-y-5'>
                    <div className='flex'>
                      <h1 className='text-2xl text-clip overflow-clip'>{birdData?.name}</h1>
                      <div className='my-auto'>
                        <Button
                          onClick={() => addToCompare(birdData)}
                          icon={<PlusOutlined />}
                          className='ml-10 !text-sm w-[full] lg:w-full relative !text-green-800 !border-green-800'
                        >
                          <span className='!text-xs'>So sánh</span>
                        </Button>
                      </div>
                    </div>
                    <Row gutter={[16, 16]}>
                      <Col span={24}>
                        <Rate value={birdData?.totalRating} allowHalf disabled />
                      </Col>
                      <Descriptions items={smallDetail} column={1} />
                    </Row>

                    <p className='w-[60%] !text-4xl text-red-500'>
                      <span>{formatCurrencyVND(birdData ? birdData.price : 0)}</span>
                    </p>

                    <div className='flex gap-5'>
                      <Tag bordered={false} color='green' className='!text-center'>
                        {birdData?.birdType}
                      </Tag>
                      <Tag bordered={false} color='green' className='!text-center'>
                        {birdData?.categoryName}
                      </Tag>
                    </div>

                    <div className='h-full w-full text-sm flex'>
                      <div className='flex w-full'>
                        <Button
                          onClick={() => handleAddToCart(birdData)}
                          icon={<ShoppingCartOutlined />}
                          className='w-[48%] !border-green-700 !text-green-700 !bg-green-50'
                        >
                          Thêm giỏ hàng
                        </Button>
                        <div className='w-[2%]'></div>

                        <Button
                          icon={<ShoppingOutlined />}
                          onClick={() => {
                            handleAddToCart(birdData)
                            navigate('/shoppingcart')
                          }}
                          className='w-[50%] !bg-green-700 !text-white'
                        >
                          Mua ngay
                        </Button>
                      </div>
                    </div>
                  </div>
                </div>
              </Col>
            </Row>
          )}
        </Skeleton>
      </div>

      {/* Thông tin Mô tả */}

      <div className='bg-gray-200 mt-5 p-5 rounded-xl'>
        <Tabs>
          <Tabs.TabPane tab='Mô tả sản phẩm' key='tab1'>
            <Skeleton loading={loading} active>
              {error ? <p>Error</p> : <Descriptions layout='vertical' items={items} />}
            </Skeleton>
          </Tabs.TabPane>
          <Tabs.TabPane id='rating' tab='Đánh giá sản phẩm' key='tab2'>
            <Skeleton loading={feedbackLoading} active>
              {feedbackError ? (
                <p>Error</p>
              ) : (
                <List
                  pagination={{ position: 'bottom', align: 'center' }}
                  dataSource={feedbackData}
                  renderItem={(item) => (
                    <List.Item>
                      <List.Item.Meta
                        avatar={<Avatar src={getUserImage(item.userImage)} icon={<UserOutlined />} />}
                        title={
                          <Space direction='vertical'>
                            <Typography.Text>{item.fullName ? item.fullName : 'Người dùng'}</Typography.Text>
                            <Rate disabled defaultValue={item.rating} />
                          </Space>
                        }
                        description={item.comment}
                      />
                    </List.Item>
                  )}
                />
              )}
            </Skeleton>
          </Tabs.TabPane>
        </Tabs>
      </div>

      {/* Sản phẩm tương tự */}

      <div className='bg-gray-200 mt-5 rounded-xl'>
        <p className='text-2xl p-5 ml-5 font-bold'>Sản phẩm tương tự</p>
        <div className='lg:mb-0 p-5  mx-auto'>
          <Skeleton loading={recommendLoading}>
            {recommendError ? (
              <Result title='Failed to fetch' subTitle={recommendError} status='error' />
            ) : (
              <Carousel responsive={responsive}>
                {recommendBird.map((item) => (
                  <div className='mx-5'>
                    <Card
                      onClick={() => navigate(`/productdetail/${item.id}`)}
                      hoverable
                      cover={
                        <Image
                          src={getBirdImage(item.thumbnail)}
                          className='text-center !w-full object-cover'
                          preview={false}
                          fallback='data:image/png;base64,'
                        />
                      }
                    >
                      <div className='flex flex-col text-xs space-y-3'>
                        <div className='flex flex-col justify-center w-full space-y-3'>
                          <Typography.Text ellipsis={true}>{item.name} </Typography.Text>
                          <p className='break-words w-full text-sm text-red-500'>{formatCurrencyVND(item.price)}</p>
                        </div>
                      </div>
                    </Card>
                  </div>
                ))}
              </Carousel>
            )}
          </Skeleton>
        </div>
      </div>
    </div>
  )
}

export default ProductDetail
