import React, { useEffect, useState } from 'react'
import { Button, Image, Skeleton, Badge, App, Space, Typography, Row, Col, Breadcrumb, Card } from 'antd'
import { Link, useNavigate } from 'react-router-dom'
import Carousel from 'react-multi-carousel'
import 'react-multi-carousel/lib/styles.css'
import { PlusOutlined, ShoppingCartOutlined } from '@ant-design/icons'
import useFetchData from '~/application/hooks/useFetchData'
import { formatCurrencyVND } from '~/utils/numberUtils'
import { useCart } from '~/application/hooks/useCart'
import { useCompare } from '~/application/hooks/useCompare'
import { getBirdImage } from '~/utils/imageUtils'
import CompareLayout from '~/application/layouts/compareLayout'

const { Title } = Typography

const responsive = {
  desktop: {
    breakpoint: {
      max: 3000,
      min: 1024
    },
    items: 3,
    partialVisibilityGutter: 40
  },
  mobile: {
    breakpoint: {
      max: 464,
      min: 0
    },
    items: 1,
    partialVisibilityGutter: 30
  },
  tablet: {
    breakpoint: {
      max: 1024,
      min: 464
    },
    items: 2,
    partialVisibilityGutter: 30
  }
}

type Bird = {
  id: number
  name: string
  categoryName: string
  birdType: string
  gender: string
  price: number
  thumbnail: string
}

const ProductList: React.FC = () => {
  const { addToCart } = useCart()
  const { addProductToCompare } = useCompare()
  const { notification } = App.useApp()
  const [recommendLoading, recommendError, recommendResponse] = useFetchData(`/birds/recommendation`)
  const [displayTop20, setDisplayTop20] = useState<Bird[]>([])
  const [bestSeller, setBestSeller] = useState<Bird[]>([])
  const navigate = useNavigate()

  const addToCompare = (bird: Bird) => {
    addProductToCompare({
      id: bird.id.toString(),
      name: bird.name,
      thumbnail: bird.thumbnail
    })
    notification.success({ message: 'Thêm vào so sánh thành công' })
  }

  const handleAddToCart = (bird: Bird) => {
    addToCart({
      id: bird.id.toString(),
      price: bird.price
    })
    notification.success({ message: 'Thêm vào giỏ hàng thành công' })
  }

  useEffect(() => {
    if (!recommendLoading && !recommendError) {
      setDisplayTop20(recommendResponse.data.top20)
      setBestSeller(recommendResponse.data.bestSellers)
    }
  }, [recommendLoading, recommendError, recommendResponse])

  return (
    <CompareLayout>
      <Skeleton loading={recommendLoading} active paragraph={{ rows: 10 }} round>
        <Space className='w-full' direction='vertical' size='large'>
          <Breadcrumb
            items={[
              {
                title: <Link to='/'>Trang chủ</Link>
              },
              {
                title: 'Shopping'
              }
            ]}
          />
          <Row style={{ background: '#038777' }} className='p-5 rounded-xl'>
            <Col span={24}>
              <Space direction='vertical' className='w-full'>
                <Title level={3} className='!text-white ml-5'>
                  Bán chạy nhất
                </Title>
                <Carousel responsive={responsive}>
                  {bestSeller.map((item) => (
                    <div className='mx-5'>
                      <Card
                        onClick={() => navigate(`/productdetail/${item.id}`)}
                        hoverable
                        cover={
                          <Badge.Ribbon text='Best Seller' className='!text-lg' color='red'>
                            <Image
                              src={getBirdImage(item.thumbnail)}
                              className='text-center !w-full object-cover '
                              preview={false}
                              fallback='data:image/png;base64,'
                            />
                          </Badge.Ribbon>
                        }
                      >
                        <div className='flex flex-col text-xs space-y-3'>
                          <div className='flex flex-col justify-center w-full space-y-3'>
                            <Typography.Text ellipsis={true}>{item.name} </Typography.Text>
                            <p className='break-words w-full text-sm text-red-500'>{formatCurrencyVND(item.price)}</p>
                          </div>
                          <Button
                            onClick={(e) => {
                              e.stopPropagation()
                              addToCompare(item)
                            }}
                            size='middle'
                            icon={<PlusOutlined />}
                            className=''
                          >
                            So sánh
                          </Button>
                          <Button
                            onClick={(e) => {
                              e.stopPropagation()
                              handleAddToCart(item)
                            }}
                            size='middle'
                            icon={<ShoppingCartOutlined />}
                            type='primary'
                          >
                            Thêm giỏ hàng
                          </Button>
                        </div>
                      </Card>
                    </div>
                  ))}
                </Carousel>
              </Space>
            </Col>
          </Row>
          <Row style={{ background: '#038777' }} className='p-5 rounded-xl'>
            <Col span={24}>
              <Space direction='vertical' className='w-full'>
                <Title level={3} className='!text-white ml-5'>
                  Top 20
                </Title>
                <Carousel responsive={responsive} transitionDuration={500}>
                  {displayTop20.map((item) => (
                    <div className='mx-5'>
                      <Card
                        onClick={() => navigate(`/productdetail/${item.id}`)}
                        hoverable
                        cover={
                          <Image
                            src={getBirdImage(item.thumbnail)}
                            className='text-center !w-full object-cover'
                            preview={false}
                            fallback='data:image/png;base64,'
                          />
                        }
                      >
                        <div className='flex flex-col text-xs space-y-3'>
                          <div className='flex flex-col justify-center w-full space-y-3'>
                            <Typography.Text ellipsis={true}>{item.name} </Typography.Text>
                            <p className='break-words w-full text-sm text-red-500'>{formatCurrencyVND(item.price)}</p>
                          </div>
                          <Button
                            onClick={(e) => {
                              e.stopPropagation()
                              addToCompare(item)
                            }}
                            size='middle'
                            icon={<PlusOutlined />}
                            className=''
                          >
                            So sánh
                          </Button>
                          <Button
                            onClick={(e) => {
                              e.stopPropagation()
                              handleAddToCart(item)
                            }}
                            size='middle'
                            icon={<ShoppingCartOutlined />}
                            type='primary'
                          >
                            Thêm giỏ hàng
                          </Button>
                        </div>
                      </Card>
                    </div>
                  ))}
                </Carousel>
              </Space>
            </Col>
          </Row>
          <Row className='w-full flex justify-center'>
            <Button className='!bg-green-800 !text-white' onClick={() => navigate('/viewall')}>
              Xem tất cả
            </Button>
          </Row>
        </Space>
      </Skeleton>
    </CompareLayout>
  )
}

export default ProductList
